import { FETCH_POSTS, NEW_POST, ADD_MESSAGE, CLEAR_MESSAGE } from './types';

export const fetchPosts = () => dispatch => {
  fetch('https://jsonplaceholder.typicode.com/posts')
    .then(res => res.json())
    .then(posts =>
      dispatch({
        type: FETCH_POSTS,
        payload: posts
      })
    );
};

export const createPost = postData => dispatch => {
  fetch('https://jsonplaceholder.typicode.com/posts', {
    method: 'POST',
    headers: {
      'content-type': 'application/json'
    },
    body: JSON.stringify(postData)
  })
    .then(res => res.json())
    .then(post =>
      dispatch({
        type: NEW_POST,
        payload: post
      })
    );
};





export const add_message = (id) => {
  return {
    type: ADD_MESSAGE,
    id,
  };
};

export const delete_message = (id) => {
  return {
    type: DELETE_MESSAGE,
    id,
  };
};

export const clear_message  = () => {
  return {
    type: CLEAR_MESSAGE,

  };
};
