  export default function(state = initialState, action) {
    switch (action.type) {
      case ADD_MESSAGE:
        return {
          ...state,
          items:[
            ...state.items,
            Object.assign({}, action.item)
          ],
        }
      case DELETE_MESSAGE:
        return {
          ...state,
          items: state.items.filter((data, i) => i!== action.id)
        };
        case CLEAR_MESSAGE:
        return {
          ...state,
          items: [],
        };
      default:
        console.log(state)
        return state;
    }
  }
